package de.brockhausag.jedichat.data;

import de.brockhausag.jedichat.auth.JediChatUserDetailsService;
import de.brockhausag.jedichat.auth.UserRole;
import de.brockhausag.jedichat.data.dto.CreateUserDto;
import de.brockhausag.jedichat.data.enums.Fraction;
import de.brockhausag.jedichat.data.enums.Gender;
import de.brockhausag.jedichat.data.enums.Species;
import de.brockhausag.jedichat.exceptions.NickNameAlreadyExistsException;
import de.brockhausag.jedichat.exceptions.PasswordsNotMatchingException;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.ApplicationListener;
import org.springframework.context.event.ContextRefreshedEvent;
import org.springframework.stereotype.Component;



@Component
public class DatabaseInititializer implements ApplicationListener<ContextRefreshedEvent> {

    private Logger log = LoggerFactory.getLogger(DatabaseInititializer.class);

    private static final String adminAvatarB64 = "";
    private static final String userAvatarB64 = "";

    private boolean alreadySetup = false;

    private final JediChatUserDetailsService userDetailsService;

    @Autowired
    public DatabaseInititializer(JediChatUserDetailsService userDetailsService) {
        this.userDetailsService = userDetailsService;
    }

    @Override
    public void onApplicationEvent(ContextRefreshedEvent event) {
        if (alreadySetup) {
            return;
        }

        alreadySetup = true;
        setupUsers();
    }

    private void setupUsers() {
        CreateUserDto admin = new CreateUserDto();
        admin.setNickName("admin");
        admin.setPassword("admin");
        admin.setMatchingPassword("admin");
        admin.setAvatar(adminAvatarB64);
        admin.setGender(Gender.FEMALE);
        admin.setSpecies(Species.TWILEK);
        admin.setBio("Hi there. I am the default admin with a super secure password. :)");
        admin.setFraction(Fraction.GALACTIC_REPUBLIC);

        CreateUserDto user = new CreateUserDto();
        user.setNickName("user");
        user.setPassword("user");
        user.setMatchingPassword("user");
        user.setAvatar(userAvatarB64);
        user.setGender(Gender.MALE);
        user.setSpecies(Species.HUMAN);
        user.setBio("I am the default user. Nice to meet you.");
        user.setFraction(Fraction.REBEL_ALLIANCE);

        log.info("Attempting to create default users.");

        try {
            userDetailsService.create(admin, UserRole.ADMIN);
            userDetailsService.create(user, UserRole.USER);
            log.info("Successfully created default users.");
        } catch (PasswordsNotMatchingException | NickNameAlreadyExistsException e) {
            log.error("Passwords did not match or user name already exists. No default users created. This should never happen wtf.", e);
        }

    }

}